using System;
using System.Collections.Generic;
using System.Drawing;
using System.Drawing.Drawing2D;
using System.Drawing.Text;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows.Forms;

namespace ASCTheme.Controls
{
    public static class Global
    {
        public static Color bgColor { get { return _bgColor; } set { _bgColor = value; } }
        public static Color SelectedColor { get { return _SelectedColor; } set { _SelectedColor = value; } }
        public static Color HeadSelectColor
        {
            get
            {
                return _HeadSelectColor;
            }
            set
            {
                if (value == Color.Empty)
                {
                    _HeadSelectColor = ColorTranslator.FromHtml("#4c79ff");
                    return;
                }
                _HeadSelectColor = value;
            }
        }

        public static Color tabSelected { get { return _tabSelected; } set { _tabSelected = value; } }
        public static Color tabNotSelecting { get { return _tabNotSelecting; } set { _tabNotSelecting = value; } }
        private static Color _bgColor = ColorTranslator.FromHtml("#0c1738");
        private static Color _SelectedColor = ColorTranslator.FromHtml("#1f2e59");
        private static Color _HeadSelectColor = ColorTranslator.FromHtml("#4c79ff");
        private static Color _tabSelected = ColorTranslator.FromHtml("#293865");
        private static Color _tabNotSelecting = ColorTranslator.FromHtml("#17244b");

        public static Image ConvertBase64ToImage(string base64)
        {
            byte[] bytes = Convert.FromBase64String(base64);
            using (MemoryStream ms = new MemoryStream(bytes, 0, bytes.Length))
            {
                Image img = Image.FromStream(ms);
                return img;
            }
        }
    }
    public class ASCTabControl : TabControl
    {
        private Graphics g;

        public int[] Value { get { return _Value; } set { _Value = value; Invalidate(); } }
        private int[] _Value;

        StringFormat stringFormat = new StringFormat();

        public ASCTabControl()
        {
            Alignment = TabAlignment.Top;
            SizeMode = TabSizeMode.Fixed;
            SetStyle(ControlStyles.UserPaint | ControlStyles.OptimizedDoubleBuffer | ControlStyles.AllPaintingInWmPaint, true);
            ItemSize = new Size(100, 103);
            Size = new Size(308, 293);
            Dock = DockStyle.Fill;

            stringFormat.Alignment = StringAlignment.Center;
            stringFormat.LineAlignment = StringAlignment.Far;
        }

        protected override void OnControlAdded(ControlEventArgs e)
        {
            base.OnControlAdded(e);
            e.Control.BackColor = Global.bgColor;
            Value = new int[TabPages.Count];
        }

        protected override void OnPaint(PaintEventArgs e)
        {
            base.OnPaint(e);
            g = e.Graphics;
            g.SmoothingMode = SmoothingMode.AntiAlias;
            g.TextRenderingHint = TextRenderingHint.ClearTypeGridFit;
            g.Clear(Global.bgColor);
            for (int i = 0; i < TabPages.Count; i++)
            {
                Rectangle tabRect = GetTabRect(i);
                if (SelectedIndex == i)
                {
                    g.FillRectangle(new SolidBrush(Global.SelectedColor), tabRect);
                    g.FillRectangle(new SolidBrush(Global.HeadSelectColor), new Rectangle(tabRect.X, tabRect.Y, tabRect.Width, 2));
                    g.DrawString(TabPages[i].Text, Font, new SolidBrush(Color.White), tabRect, stringFormat);
                    g.FillPie(new SolidBrush(ColorTranslator.FromHtml("#374369")), new Rectangle(tabRect.X + 20, tabRect.Y + 17, 60, 60), 0, 360);
                    g.FillPie(new SolidBrush(Global.HeadSelectColor), new Rectangle(tabRect.X + 20, tabRect.Y + 17, 60, 60), 270, (float)Math.Round((double)(Value[i] * 360) / 100));
                    g.FillPie(new SolidBrush(ColorTranslator.FromHtml("#1f2e59")), new Rectangle(tabRect.X + 23, tabRect.Y + 20, 54, 54), 0, 360);
                }
                else
                {
                    g.DrawString(TabPages[i].Text, Font, new SolidBrush(Color.Silver), tabRect, stringFormat);
                    g.FillPie(new SolidBrush(ColorTranslator.FromHtml("#374369")), new Rectangle(tabRect.X + 20, tabRect.Y + 17, 60, 60), 0, 360);
                    g.FillPie(new SolidBrush(Global.HeadSelectColor), new Rectangle(tabRect.X + 20, tabRect.Y + 17, 60, 60), 270, (float)Math.Round((double)(Value[i] * 360) / 100));
                    g.FillPie(new SolidBrush(Global.bgColor), new Rectangle(tabRect.X + 23, tabRect.Y + 20, 54, 54), 0, 360);
                }
                if (TabPages[i].ImageIndex > -1)
                    g.DrawImage(Utilities.Hue(new Bitmap(ImageList.Images[0])), new Rectangle(tabRect.X + (tabRect.Width / 2) - 14, tabRect.Y + (tabRect.Height / 2) - 18, 30, 30));
                else
                    g.DrawString(string.Format("{0}%", Value[i].ToString()), new Font("Segoe UI", 10f, FontStyle.Bold), new SolidBrush(Global.HeadSelectColor), new Rectangle(tabRect.X, tabRect.Y / 2, tabRect.Width + 5, (tabRect.Height / 2) + 8), stringFormat);
            }
        }
    }

    public class ASCTabControlSide : TabControl
    {

        private Graphics g;
        //StringFormat stringFormat = new StringFormat();

        public TabIconEnum[] TabIcon { get; set; }

        private string OKIcon = "";
        private string CancelIcon = "";
        //private string LoadingIcon = "";

        public enum TabIconEnum
        {
            None,
            //Loading,
            OK,
            Cancel
        }

        public ASCTabControlSide()
        {
            Alignment = TabAlignment.Left;
            DoubleBuffered = true;
            SetStyle(ControlStyles.UserPaint, true);
            SizeMode = TabSizeMode.Fixed;
            ItemSize = new Size(50, 305);
            Size = new Size(500, 245);
        }

        protected override void OnControlAdded(ControlEventArgs e)
        {
            base.OnControlAdded(e);
            TabIcon = new TabIconEnum[TabPages.Count];
            e.Control.BackColor = Global.tabNotSelecting;
        }

        protected override void OnPaint(PaintEventArgs e)
        {
            base.OnPaint(e);
            g = e.Graphics;
            g.SmoothingMode = System.Drawing.Drawing2D.SmoothingMode.HighQuality;
            g.TextRenderingHint = System.Drawing.Text.TextRenderingHint.ClearTypeGridFit;
            g.Clear(Global.SelectedColor);

            for (int i = 0; i < TabPages.Count; i++)
            {
                Rectangle tabRect = GetTabRect(i);
                //TabPages[i].BackColor = Global.tabNotSelecting;
                if (SelectedIndex == i)
                {
                    g.FillRectangle(new SolidBrush(Global.tabSelected), tabRect);
                    g.FillRectangle(new SolidBrush(Global.HeadSelectColor), new Rectangle(tabRect.X, tabRect.Y, 2, tabRect.Height));
                    g.DrawString(TabPages[i].Text, new Font("Segoe UI", 9), new SolidBrush(Color.White), new Point(50, tabRect.Y + (tabRect.Height / 3)));
                }
                else
                {
                    g.DrawString(TabPages[i].Text, new Font("Segoe UI", 9), new SolidBrush(Color.Gray), new Point(50, tabRect.Y + (tabRect.Height / 3)));
                }
                if (TabIcon[i] == TabIconEnum.OK)
                    g.DrawImage(Global.ConvertBase64ToImage(OKIcon), new Point(270, (tabRect.Y + (tabRect.Height / 3))));
                else if (TabIcon[i] == TabIconEnum.Cancel)
                    g.DrawImage(Global.ConvertBase64ToImage(CancelIcon), new Point(270, (tabRect.Y + (tabRect.Height / 3))));
                //else if (TabIcon[i] == Icon.Loading)
                    //g.DrawImage(Global.ConvertBase64ToImage(LoadingIcon), new Point(270, (tabRect.Y + (tabRect.Height / 3))));
            }
        }
    }
}
